import "./App.module.css";
import { useEffect } from "react";
function App() {
  useEffect(() => {
    (function () {
      const second = 1000,
        minute = second * 60,
        hour = minute * 60,
        day = hour * 24;

      let birthday = "Sep 30, 2021 00:00:00",
        countDown = new Date(birthday).getTime(),
        x = setInterval(function () {
          let now = new Date().getTime(),
            distance = countDown - now;

          (document.getElementByclassName("days").innerText = Math.floor(
            distance / day
          )),
            (document.getElementByclassName("hours").innerText = Math.floor(
              (distance % day) / hour
            )),
            (document.getElementByclassName("minutes").innerText = Math.floor(
              (distance % hour) / minute
            )),
            (document.getElementByclassName("seconds").innerText = Math.floor(
              (distance % minute) / second
            ));

          //do something later when date is reached
          if (distance < 0) {
            let headline = document.getElementByclassName("headline"),
              countdown = document.getElementByclassName("countdown"),
              content = document.getElementByclassName("content");

            headline.innerText = "It's my birthday!";
            countdown.style.display = "none";
            content.style.display = "block";

            clearInterval(x);
          }
          //seconds
        }, 0);
    })();
  }, []);
  return (
    <div className="container">
      <h3 className="headline">Countdown to the launch</h3>
      <div className="countdown">
        <ul>
          <li>
            <span className="days"></span>days
          </li>
          <li>
            <span className="hours"></span>Hours
          </li>
          <li>
            <span className="minutes"></span>Minutes
          </li>
          <li>
            <span className="seconds"></span>Seconds
          </li>
        </ul>
      </div>
      
    </div>
  );
}

export default App;
