import React from "react";

function test() {
  useEffect(() => {
    const form = document.getElementById("form");
    const username = document.getElementById("username");
    const email = document.getElementById("email");
    const password = document.getElementById("password");
    const password2 = document.getElementById("password2");

    form.addEventListener("submit", (e) => {
      e.preventDefault();

      checkInputs();
    });

    function checkInputs() {
      // trim to remove the whitespaces
      const usernameValue = username.value.trim();
      const emailValue = email.value.trim();
      const passwordValue = password.value.trim();
      const password2Value = password2.value.trim();

      if (usernameValue === "") {
        setErrorFor(username, "Username cannot be blank");
      } else {
        setSuccessFor(username);
      }

      if (emailValue === "") {
        setErrorFor(email, "Email cannot be blank");
      } else if (!isEmail(emailValue)) {
        setErrorFor(email, "Not a valid email");
      } else {
        setSuccessFor(email);
      }

      if (passwordValue === "") {
        setErrorFor(password, "Password cannot be blank");
      } else {
        setSuccessFor(password);
      }

      if (password2Value === "") {
        setErrorFor(password2, "Password2 cannot be blank");
      } else if (passwordValue !== password2Value) {
        setErrorFor(password2, "Passwords does not match");
      } else {
        setSuccessFor(password2);
      }
    }

    function setErrorFor(input, message) {
      const formControl = input.parentElement;
      const small = formControl.querySelector("small");
      formControl.className = "form-control error";
      small.innerText = message;
    }

    function setSuccessFor(input) {
      const formControl = input.parentElement;
      formControl.className = "form-control success";
    }

    function isEmail(email) {
      return /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(
        email
      );
    }

    // SOCIAL PANEL JS
    const floating_btn = document.querySelector(".floating-btn");
    const close_btn = document.querySelector(".close-btn");
    const social_panel_container = document.querySelector(
      ".social-panel-container"
    );

    floating_btn.addEventListener("click", () => {
      social_panel_container.classList.toggle("visible");
    });

    close_btn.addEventListener("click", () => {
      social_panel_container.classList.remove("visible");
    });
  }, []);
  return (
    <div>
      <div className="container">
        <div className="header">
          <h2>Create Account</h2>
        </div>
        <form id="form" className="form">
          <div className="form-control">
            <label for="username">Username</label>
            <input type="text" placeholder="florinpop17" id="username" />
            <i className="fas fa-check-circle"></i>
            <i className="fas fa-exclamation-circle"></i>
            <small>Error message</small>
          </div>
          <div className="form-control">
            <label for="username">Email</label>
            <input type="email" placeholder="a@florin-pop.com" id="email" />
            <i className="fas fa-check-circle"></i>
            <i className="fas fa-exclamation-circle"></i>
            <small>Error message</small>
          </div>
          <div className="form-control">
            <label for="username">Password</label>
            <input type="password" placeholder="Password" id="password" />
            <i className="fas fa-check-circle"></i>
            <i className="fas fa-exclamation-circle"></i>
            <small>Error message</small>
          </div>
          <div className="form-control">
            <label for="username">Password check</label>
            <input type="password" placeholder="Password two" id="password2" />
            <i className="fas fa-check-circle"></i>
            <i className="fas fa-exclamation-circle"></i>
            <small>Error message</small>
          </div>
          <button>Submit</button>
        </form>
      </div>

      <div className="social-panel-container">
        <div className="social-panel">
          <p>
            Created with <i className="fa fa-heart"></i> by
            <a target="_blank" href="https://florin-pop.com">
              Florin Pop
            </a>
          </p>
          <button className="close-btn">
            <i className="fas fa-times"></i>
          </button>
          <h4>Get in touch on</h4>
          <ul>
            <li>
              <a href="https://www.patreon.com/florinpop17" target="_blank">
                <i className="fab fa-discord"></i>
              </a>
            </li>
            <li>
              <a href="https://twitter.com/florinpop1705" target="_blank">
                <i className="fab fa-twitter"></i>
              </a>
            </li>
            <li>
              <a href="https://linkedin.com/in/florinpop17" target="_blank">
                <i className="fab fa-linkedin"></i>
              </a>
            </li>
            <li>
              <a href="https://facebook.com/florinpop17" target="_blank">
                <i className="fab fa-facebook"></i>
              </a>
            </li>
            <li>
              <a href="https://instagram.com/florinpop17" target="_blank">
                <i className="fab fa-instagram"></i>
              </a>
            </li>
          </ul>
        </div>
      </div>
      <button className="floating-btn">Get in Touch</button>

      <div className="floating-text">
        Part of{" "}
        <a
          href="https://florin-pop.com/blog/2019/09/100-days-100-projects"
          target="_blank"
        >
          #100Days100Projects
        </a>
      </div>
    </div>
  );
}

export default test;
